    37→/**
    38→ * Fetch models from CodeMie SSO API
    39→ */
    40→export async function fetchCodeMieModels(
    41→  apiUrl: string,
    42→  cookies: Record<string, string>
    43→): Promise<string[]> {
    44→  const cookieString = Object.entries(cookies)
    45→    .map(([key, value]) => `${key}=${value}`)
    46→    .join(';');
    47→
    48→  const url = `${apiUrl}${CODEMIE_ENDPOINTS.MODELS}`;
    49→
    50→  const client = new HTTPClient({
    51→    timeout: 10000,
    52→    maxRetries: 3,
    53→    rejectUnauthorized: false
    54→  });
    55→
    56→  const cliVersion = process.env.CODEMIE_CLI_VERSION || 'unknown';
    57→
    58→  const response = await client.getRaw(url, {
    59→    'cookie': cookieString,
    60→    'Content-Type': 'application/json',
    61→    'User-Agent': `codemie-cli/${cliVersion}`,
    62→    'X-CodeMie-CLI': `codemie-cli/${cliVersion}`,
    63→    'X-CodeMie-Client': 'codemie-cli'
    64→  });
    65→
    66→  if (!response.statusCode || response.statusCode < 200 || response.statusCode >= 300) {
    67→    if (response.statusCode === 401 || response.statusCode === 403) {
    68→      throw new Error('SSO session expired - please run setup again');
    69→    }
    70→    throw new Error(`Failed to fetch models: ${response.statusCode} ${response.statusMessage}`);
    71→  }
    72→
    73→  // Parse the response
    74→  const models: CodeMieModel[] = JSON.parse(response.data) as CodeMieModel[];
    75→
    76→  if (!Array.isArray(models)) {
    77→    return [];
    78→  }
    79→
    80→  // Filter and map models based on the actual API response structure
    81→  const filteredModels = models
    82→    .filter(model => {
    83→      if (!model) return false;
    84→      // Check for different possible model ID fields
    85→      const hasId = model.id && model.id.trim() !== '';
    86→      const hasBaseName = model.base_name && model.base_name.trim() !== '';
    87→      const hasDeploymentName = model.deployment_name && model.deployment_name.trim() !== '';
    88→
    89→      return hasId || hasBaseName || hasDeploymentName;
    90→    })
    91→    .map(model => {
    92→      // Use the most appropriate identifier field
    93→      return model.id || model.base_name || model.deployment_name || model.label || 'unknown';
    94→    })
    95→    .filter(id => id !== 'unknown')
    96→    .sort();
    97→
    98→  return filteredModels;
    99→}
