   200→/**
   201→ * Fetch integrations from CodeMie SSO API (paginated)
   202→ */
   203→export async function fetchCodeMieIntegrations(
   204→  apiUrl: string,
   205→  cookies: Record<string, string>,
   206→  endpointPath: string = CODEMIE_ENDPOINTS.USER_SETTINGS
   207→): Promise<CodeMieIntegration[]> {
   208→  const cookieString = Object.entries(cookies)
   209→    .map(([key, value]) => `${key}=${value}`)
   210→    .join(';');
   211→
   212→  const allIntegrations: CodeMieIntegration[] = [];
   213→  let currentPage = 0;
   214→  const perPage = 50;
   215→  let hasMorePages = true;
   216→  let lastError: Error | undefined;
   217→
   218→  while (hasMorePages) {
   219→    try {
   220→      // Build URL with query parameters to filter by LiteLLM type
   221→      const filters = JSON.stringify({ type: ['LiteLLM'] });
   222→      const queryParams = new URLSearchParams({
   223→        page: currentPage.toString(),
   224→        per_page: perPage.toString(),
   225→        filters: filters
   226→      });
   227→
   228→      const fullUrl = `${apiUrl}${endpointPath}?${queryParams.toString()}`;
   229→
   230→      if (process.env.CODEMIE_DEBUG) {
   231→        console.log(`[DEBUG] Fetching integrations from: ${fullUrl}`);
   232→      }
   233→
   234→      const pageIntegrations = await fetchIntegrationsPage(fullUrl, cookieString);
   235→
   236→      if (pageIntegrations.length === 0) {
   237→        hasMorePages = false;
   238→      } else {
   239→        allIntegrations.push(...pageIntegrations);
   240→
   241→        // If we got fewer items than requested, we've reached the last page
   242→        if (pageIntegrations.length < perPage) {
   243→          hasMorePages = false;
   244→        } else {
   245→          currentPage++;
   246→        }
   247→      }
   248→    } catch (error) {
   249→      lastError = error instanceof Error ? error : new Error(String(error));
   250→      hasMorePages = false;
   251→    }
   252→  }
   253→
   254→  // If we got no integrations and had an error, throw it
   255→  if (allIntegrations.length === 0 && lastError) {
   256→    throw lastError;
   257→  }
   258→
   259→  return allIntegrations;
   260→}
