   262→/**
   263→ * Fetch single page of integrations
   264→ */
   265→async function fetchIntegrationsPage(fullUrl: string, cookieString: string): Promise<CodeMieIntegration[]> {
   266→  const client = new HTTPClient({
   267→    timeout: 10000,
   268→    maxRetries: 3,
   269→    rejectUnauthorized: false
   270→  });
   271→
   272→  const cliVersion = process.env.CODEMIE_CLI_VERSION || 'unknown';
   273→
   274→  const response = await client.getRaw(fullUrl, {
   275→    'cookie': cookieString,
   276→    'Content-Type': 'application/json',
   277→    'User-Agent': `codemie-cli/${cliVersion}`,
   278→    'X-CodeMie-CLI': `codemie-cli/${cliVersion}`,
   279→    'X-CodeMie-Client': 'codemie-cli'
   280→  });
   281→
   282→  if (!response.statusCode || response.statusCode < 200 || response.statusCode >= 300) {
   283→    if (response.statusCode === 401 || response.statusCode === 403) {
   284→      throw new Error('SSO session expired - please run setup again');
   285→    }
   286→    if (response.statusCode === 404) {
   287→      throw new Error(`Integrations endpoint not found. Response: ${response.data}`);
   288→    }
   289→    throw new Error(`Failed to fetch integrations: ${response.statusCode} ${response.statusMessage}`);
   290→  }
   291→
   292→  // Parse the response - handle flexible response structure
   293→  if (process.env.CODEMIE_DEBUG) {
   294→    console.log('[DEBUG] Integration API response:', response.data.substring(0, 500));
   295→  }
   296→
   297→  const responseData = JSON.parse(response.data) as CodeMieIntegrationsResponse;
   298→
   299→  // Extract integrations from response - try all possible locations
   300→  let integrations: CodeMieIntegration[] = [];
   301→
   302→  // Try different possible property names and structures
   303→  const possibleArrays = [
   304→    responseData, // Direct array
   305→    responseData.integrations,
   306→    responseData.credentials,
   307→    responseData.data,
   308→    responseData.items,
   309→    responseData.results,
   310→    responseData.user_integrations,
   311→    responseData.personal_integrations,
   312→    responseData.available_integrations
   313→  ].filter(arr => Array.isArray(arr));
   314→
   315→  if (possibleArrays.length > 0) {
   316→    integrations = possibleArrays[0] as CodeMieIntegration[];
   317→  } else {
   318→    // Try to find nested objects that might contain arrays
   319→    for (const value of Object.values(responseData)) {
   320→      if (typeof value === 'object' && value !== null) {
   321→        const nestedArrays = Object.values(value).filter(Array.isArray);
   322→        if (nestedArrays.length > 0) {
   323→          integrations = nestedArrays[0] as CodeMieIntegration[];
   324→          break;
   325→        }
   326→      }
   327→    }
   328→  }
   329→
   330→  // Filter and validate integrations (already filtered by API, but double-check)
   331→  const validIntegrations = integrations
   332→    .filter(integration => {
   333→      return integration &&
   334→             integration.alias &&
   335→             integration.credential_type &&
   336→             integration.alias.trim() !== '' &&
   337→             integration.credential_type.trim() !== '';
   338→    })
   339→    .sort((a, b) => a.alias.localeCompare(b.alias));
   340→
   341→  return validIntegrations;
   342→}
