     1→/**
     2→ * LiteLLM Model Proxy
     3→ *
     4→ * Fetches available models from LiteLLM proxy server via /v1/models endpoint.
     5→ */
     6→
     7→import type { CodeMieConfigOptions } from '../../../env/types.js';
     8→import type { ModelInfo } from '../../core/types.js';
     9→import { BaseModelProxy } from '../../core/base/BaseModelProxy.js';
    10→
    11→/**
    12→ * LiteLLM model proxy implementation
    13→ *
    14→ * Fetches models from LiteLLM's OpenAI-compatible /v1/models endpoint
    15→ */
    16→export class LiteLLMModelProxy extends BaseModelProxy {
    17→  constructor(baseUrl: string, apiKey?: string) {
    18→    super(baseUrl, 10000);
    19→    this.apiKey = apiKey;
    20→  }
    21→
    22→  private apiKey?: string;
    23→
    24→  supports(provider: string): boolean {
    25→    return provider === 'litellm';
    26→  }
    27→
    28→  /**
    29→   * List models from LiteLLM proxy
    30→   */
    31→  async listModels(): Promise<ModelInfo[]> {
    32→    const headers: Record<string, string> = {
    33→      'Content-Type': 'application/json'
    34→    };
    35→
    36→    // Add API key if provided
    37→    if (this.apiKey && this.apiKey !== 'not-required') {
    38→      headers['Authorization'] = `Bearer ${this.apiKey}`;
    39→    }
    40→
    41→    const response = await this.client.get<{ data: Array<{ id: string }> }>(
    42→      `${this.baseUrl}/v1/models`,
    43→      headers
    44→    );
    45→
    46→    // Transform OpenAI format to ModelInfo
    47→    const models: ModelInfo[] = response.data.data.map(model => ({
    48→      id: model.id,
    49→      name: model.id,
    50→      popular: false
    51→    }));
    52→
    53→    return models;
    54→  }
    55→
    56→  /**
    57→   * Fetch models for setup wizard
    58→   */
    59→  async fetchModels(_config: CodeMieConfigOptions): Promise<ModelInfo[]> {
    60→    return this.listModels();
    61→  }
    62→}
