     4→import { logger } from '../utils/logger.js';
     5→
     6→export interface EnvConfig {
     7→  [key: string]: string;
     8→}
     9→
    10→export class EnvManager {
    11→  private static CONFIG_DIR = path.join(os.homedir(), '.codemie');
    12→  private static CONFIG_FILE = path.join(EnvManager.CONFIG_DIR, 'codemie-cli.config.json');
    13→
    14→  static async loadGlobalConfig(): Promise<EnvConfig> {
    15→    try {
    16→      const content = await fs.readFile(EnvManager.CONFIG_FILE, 'utf-8');
    17→      return JSON.parse(content);
    18→    } catch {
    19→      return {};
    20→    }
    21→  }
    22→
    23→  static async saveGlobalConfig(config: EnvConfig): Promise<void> {
    24→    try {
    25→      await fs.mkdir(EnvManager.CONFIG_DIR, { recursive: true });
    26→      await fs.writeFile(
    27→        EnvManager.CONFIG_FILE,
    28→        JSON.stringify(config, null, 2),
    29→        'utf-8'
    30→      );
    31→      logger.success(`Configuration saved to ${EnvManager.CONFIG_FILE}`);
    32→    } catch (error: unknown) {
    33→      logger.error('Failed to save configuration:', error);
    34→      throw error;
    35→    }
    36→  }
    37→
    38→  static async getConfigValue(key: string): Promise<string | undefined> {
    39→    // Priority: process.env > global config
    40→    if (process.env[key]) {
    41→      return process.env[key];
    42→    }
    43→
    44→    const config = await EnvManager.loadGlobalConfig();
    45→    return config[key];
    46→  }
    47→
    48→  static async setConfigValue(key: string, value: string): Promise<void> {
    49→    const config = await EnvManager.loadGlobalConfig();
    50→    config[key] = value;
    51→    await EnvManager.saveGlobalConfig(config);
    52→  }
    53→
    54→  static async deleteConfigValue(key: string): Promise<void> {
    55→    const config = await EnvManager.loadGlobalConfig();
    56→    delete config[key];
    57→    await EnvManager.saveGlobalConfig(config);
    58→  }
    59→
    60→  static async showConfig(): Promise<void> {
    61→    const config = await EnvManager.loadGlobalConfig();
    62→
    63→    if (Object.keys(config).length === 0) {
    64→      logger.info('No configuration found');
    65→      return;
    66→    }
    67→
    68→    logger.info('Global configuration:');
    69→    for (const [key, value] of Object.entries(config)) {
    70→      // Mask sensitive values
