     1‚Üíimport { Command } from 'commander';
     2‚Üíimport { readFileSync } from 'fs';
     3‚Üíimport { join } from 'path';
     4‚Üíimport chalk from 'chalk';
     5‚Üíimport { AgentAdapter } from './types.js';
     6‚Üíimport { ConfigLoader, CodeMieConfigOptions } from '../../utils/config-loader.js';
     7‚Üíimport { logger } from '../../utils/logger.js';
     8‚Üíimport { getDirname } from '../../utils/dirname.js';
     9‚Üíimport { BUILTIN_AGENT_NAME } from '../registry.js';
    10‚Üíimport { ClaudePluginMetadata } from '../plugins/claude.plugin.js';
    11‚Üíimport { CodexPluginMetadata } from '../plugins/codex.plugin.js';
    12‚Üíimport { CodeMieCodePluginMetadata } from '../plugins/codemie-code.plugin.js';
    13‚Üíimport { GeminiPluginMetadata } from '../plugins/gemini.plugin.js';
    14‚Üíimport { DeepAgentsPluginMetadata } from '../plugins/deepagents.plugin.js';
    15‚Üí
    16‚Üí/**
    17‚Üí * Universal CLI builder for any agent
    18‚Üí * Builds commander programs from agent metadata
    19‚Üí */
    20‚Üíexport class AgentCLI {
    21‚Üí  private program: Command;
    22‚Üí  private version: string = '1.0.0';
    23‚Üí
    24‚Üí  constructor(private adapter: AgentAdapter) {
    25‚Üí    this.program = new Command();
    26‚Üí    this.loadVersion();
    27‚Üí    this.setupProgram();
    28‚Üí
    29‚Üí    // Set agent name in logger for consistent log formatting
    30‚Üí    logger.setAgentName(adapter.name);
    31‚Üí  }
    32‚Üí
    33‚Üí  /**
    34‚Üí   * Load version from package.json
    35‚Üí   */
    36‚Üí  private loadVersion(): void {
    37‚Üí    try {
    38‚Üí      const packageJsonPath = join(getDirname(import.meta.url), '../../../package.json');
    39‚Üí      const packageJsonContent = readFileSync(packageJsonPath, 'utf-8');
    40‚Üí      const packageJson = JSON.parse(packageJsonContent);
    41‚Üí      this.version = packageJson.version;
    42‚Üí    } catch {
    43‚Üí      // Use default version
    44‚Üí    }
    45‚Üí  }
    46‚Üí
    47‚Üí  /**
    48‚Üí   * Setup commander program
    49‚Üí   */
    50‚Üí  private setupProgram(): void {
    51‚Üí    // Handle special case where adapter name already includes 'codemie-' prefix (built-in agent)
    52‚Üí    const programName = this.adapter.name.startsWith('codemie-')
    53‚Üí      ? this.adapter.name
    54‚Üí      : `codemie-${this.adapter.name}`;
    55‚Üí
    56‚Üí    this.program
    57‚Üí      .name(programName)
    58‚Üí      .description(`CodeMie ${this.adapter.displayName} - ${this.adapter.description}`)
    59‚Üí      .version(this.version)
    60‚Üí      .option('--profile <name>', 'Use specific provider profile')
    61‚Üí      .option('--provider <provider>', 'Override provider (ai-run-sso, litellm, ollama)')
    62‚Üí      .option('-m, --model <model>', 'Override model')
    63‚Üí      .option('--api-key <key>', 'Override API key')
    64‚Üí      .option('--base-url <url>', 'Override base URL')
    65‚Üí      .option('--timeout <seconds>', 'Override timeout (in seconds)', parseInt)
    66‚Üí      .option('--task <prompt>', 'Execute a single task (agent-specific flag mapping)')
    67‚Üí      .allowUnknownOption()
    68‚Üí      .argument('[args...]', `Arguments to pass to ${this.adapter.displayName}`)
    69‚Üí      .action(async (args, options) => {
    70‚Üí        await this.handleRun(args, options);
    71‚Üí      });
    72‚Üí
    73‚Üí    // Add health check command
    74‚Üí    this.program
    75‚Üí      .command('health')
    76‚Üí      .description(`Check ${this.adapter.displayName} health and installation`)
    77‚Üí      .action(async () => {
    78‚Üí        await this.handleHealthCheck();
    79‚Üí      });
    80‚Üí
    81‚Üí    // Add init command for frameworks (skip for built-in agent and deepagents)
    82‚Üí    if (this.adapter.name !== BUILTIN_AGENT_NAME && this.adapter.name !== 'deepagents') {
    83‚Üí      this.program
    84‚Üí        .command('init')
    85‚Üí        .description('Initialize development framework')
    86‚Üí        .argument('[framework]', 'Framework to initialize (speckit, bmad)')
    87‚Üí        .option('-l, --list', 'List available frameworks')
    88‚Üí        .option('--force', 'Force re-initialization')
    89‚Üí        .option('--project-name <name>', 'Project name for framework initialization')
    90‚Üí        .action(async (framework, options) => {
    91‚Üí          await this.handleInit(framework, options);
    92‚Üí        });
    93‚Üí    }
    94‚Üí  }
    95‚Üí
    96‚Üí  /**
    97‚Üí   * Handle main run action
    98‚Üí   */
    99‚Üí  private async handleRun(args: string[], options: Record<string, unknown>): Promise<void> {
   100‚Üí    try {
   101‚Üí      // Check if agent is installed
   102‚Üí      if (!(await this.adapter.isInstalled())) {
   103‚Üí        console.log(chalk.red(`\n‚úó ${this.adapter.displayName} is not installed\n`));
   104‚Üí        console.log(chalk.white('Install it with:\n'));
   105‚Üí        console.log(chalk.cyan(`  codemie install ${this.adapter.name}\n`));
   106‚Üí        process.exit(1);
   107‚Üí      }
   108‚Üí
   109‚Üí      // Load configuration with CLI overrides
   110‚Üí      const config = await ConfigLoader.load(process.cwd(), {
   111‚Üí        name: options.profile as string | undefined,  // Profile selection
   112‚Üí        provider: options.provider as string | undefined,
   113‚Üí        model: options.model as string | undefined,
   114‚Üí        apiKey: options.apiKey as string | undefined,
   115‚Üí        baseUrl: options.baseUrl as string | undefined,
   116‚Üí        timeout: options.timeout as number | undefined
   117‚Üí      });
   118‚Üí
   119‚Üí      // Validate essential configuration
   120‚Üí      const missingFields: string[] = [];
   121‚Üí      if (!config.baseUrl) missingFields.push('baseUrl');
   122‚Üí      if (!config.model) missingFields.push('model');
   123‚Üí
   124‚Üí      // Only validate apiKey for providers that require authentication
   125‚Üí      const { ProviderRegistry } = await import('../../providers/core/registry.js');
   126‚Üí      const provider = config.provider ? ProviderRegistry.getProvider(config.provider) : null;
   127‚Üí      const requiresAuth = provider?.requiresAuth ?? true; // Default to true for safety
   128‚Üí
   129‚Üí      if (requiresAuth && !config.apiKey) {
   130‚Üí        missingFields.push('apiKey');
   131‚Üí      }
   132‚Üí
   133‚Üí      if (missingFields.length > 0) {
   134‚Üí        console.log(chalk.yellow('\n‚ö†Ô∏è  Configuration incomplete'));
   135‚Üí        console.log(chalk.white('Missing: ') + chalk.red(missingFields.join(', ')));
   136‚Üí        console.log(chalk.white('Run ') + chalk.cyan('codemie setup') + chalk.white(' to configure your AI provider.\n'));
   137‚Üí        process.exit(1);
   138‚Üí      }
   139‚Üí
   140‚Üí      // NEW: Auth validation via provider plugin
   141‚Üí      try {
   142‚Üí        // Check if provider implements auth validation
   143‚Üí        const setupSteps = provider ? ProviderRegistry.getSetupSteps(config.provider || '') : null;
   144‚Üí
   145‚Üí        if (setupSteps?.validateAuth) {
   146‚Üí          const validationResult = await setupSteps.validateAuth(config);
   147‚Üí
   148‚Üí          if (!validationResult.valid) {
   149‚Üí            const { handleAuthValidationFailure } = await import('../../providers/core/auth-validation.js');
   150‚Üí            const reauthed = await handleAuthValidationFailure(validationResult, setupSteps, config);
   151‚Üí
   152‚Üí            if (!reauthed) {
   153‚Üí              console.log(chalk.yellow('\n‚ö†Ô∏è  Authentication required\n'));
   154‚Üí              process.exit(1);
   155‚Üí            }
   156‚Üí          }
   157‚Üí        }
   158‚Üí      } catch (error) {
   159‚Üí        logger.error('Auth validation failed:', error);
   160‚Üí        console.log(chalk.red('\n‚úó Authentication check failed\n'));
   161‚Üí        process.exit(1);
   162‚Üí      }
   163‚Üí
   164‚Üí      // Validate provider and model compatibility
   165‚Üí      if (!this.validateCompatibility(config)) {
   166‚Üí        process.exit(1);
   167‚Üí      }
   168‚Üí
   169‚Üí      const providerEnv = ConfigLoader.exportProviderEnvVars(config);
   170‚Üí
   171‚Üí      // Pass config info for welcome message display
   172‚Üí      providerEnv.CODEMIE_PROFILE_NAME = config.name || 'default';
   173‚Üí      providerEnv.CODEMIE_CLI_VERSION = this.version;
   174‚Üí
   175‚Üí      // Serialize full profile config for proxy plugins (read once at CLI level)
   176‚Üí      providerEnv.CODEMIE_PROFILE_CONFIG = JSON.stringify(config);
   177‚Üí
   178‚Üí      // Set profile name in logger for log formatting
   179‚Üí      logger.setProfileName(config.name || 'default');
   180‚Üí
   181‚Üí      // Collect all arguments to pass to the agent
   182‚Üí      const agentArgs = this.collectPassThroughArgs(args, options);
   183‚Üí
   184‚Üí      // Run the agent (welcome message will be shown inside)
   185‚Üí      await this.adapter.run(agentArgs, providerEnv);
   186‚Üí    } catch (error) {
   187‚Üí      // Show user-friendly error message in console first
   188‚Üí      const errorMessage = error instanceof Error ? error.message : String(error);
   189‚Üí      console.error(chalk.red(`\n‚úó Failed to run ${this.adapter.displayName}\n`));
   190‚Üí      console.error(chalk.white(errorMessage));
   191‚Üí      console.error('');
   192‚Üí
   193‚Üí      // Log detailed error for debugging
   194‚Üí      logger.error(`Failed to run ${this.adapter.displayName}:`, error);
   195‚Üí      process.exit(1);
   196‚Üí    }
   197‚Üí  }
   198‚Üí
   199‚Üí  /**
   200‚Üí   * Handle health check command
   201‚Üí   */
   202‚Üí  private async handleHealthCheck(): Promise<void> {
   203‚Üí    try {
   204‚Üí      if (await this.adapter.isInstalled()) {
   205‚Üí        const version = await this.adapter.getVersion();
   206‚Üí        logger.success(`${this.adapter.displayName} is installed and ready`);
   207‚Üí        if (version) {
   208‚Üí          console.log(`Version: ${version}`);
   209‚Üí        }
   210‚Üí      } else {
   211‚Üí        console.log(chalk.red(`\n‚úó ${this.adapter.displayName} is not installed\n`));
   212‚Üí        console.log(chalk.white('Install it with:\n'));
   213‚Üí        console.log(chalk.cyan(`  codemie install ${this.adapter.name}\n`));
   214‚Üí        process.exit(1);
   215‚Üí      }
   216‚Üí    } catch (error) {
   217‚Üí      logger.error('Health check failed:', error);
   218‚Üí      process.exit(1);
   219‚Üí    }
   220‚Üí  }
   221‚Üí
   222‚Üí  /**
   223‚Üí   * Handle framework initialization command
   224‚Üí   */
   225‚Üí  private async handleInit(framework: string | undefined, options: Record<string, unknown>): Promise<void> {
   226‚Üí    try {
   227‚Üí      // Import framework registry
   228‚Üí      const { FrameworkRegistry } = await import('../../frameworks/index.js');
   229‚Üí
   230‚Üí      // Handle --list flag
   231‚Üí      if (options.list) {
   232‚Üí        const frameworks = FrameworkRegistry.getFrameworksForAgent(this.adapter.name);
   233‚Üí
   234‚Üí        if (frameworks.length === 0) {
   235‚Üí          console.log(chalk.yellow('\n‚ö†Ô∏è  No frameworks available for this agent\n'));
   236‚Üí          return;
   237‚Üí        }
   238‚Üí
   239‚Üí        console.log(chalk.bold('\nüì¶ Available Frameworks:\n'));
   240‚Üí        for (const fw of frameworks) {
   241‚Üí          console.log(chalk.cyan(`  ${fw.metadata.name}`) + chalk.white(` - ${fw.metadata.description}`));
   242‚Üí          if (fw.metadata.docsUrl) {
   243‚Üí            console.log(chalk.gray(`    Docs: ${fw.metadata.docsUrl}`));
   244‚Üí          }
   245‚Üí        }
   246‚Üí        console.log('');
   247‚Üí        return;
   248‚Üí      }
   249‚Üí
   250‚Üí      // Framework name is required if not listing
   251‚Üí      if (!framework) {
   252‚Üí        console.log(chalk.red('\n‚úó Framework name is required\n'));
   253‚Üí        console.log(chalk.white('Usage:\n'));
   254‚Üí        console.log(chalk.cyan(`  codemie-${this.adapter.name} init <framework>\n`));
   255‚Üí        console.log(chalk.white('List available frameworks:\n'));
   256‚Üí        console.log(chalk.cyan(`  codemie-${this.adapter.name} init --list\n`));
   257‚Üí        process.exit(1);
   258‚Üí      }
   259‚Üí
   260‚Üí      // Get framework adapter
   261‚Üí      const frameworkAdapter = FrameworkRegistry.getFramework(framework);
   262‚Üí      if (!frameworkAdapter) {
   263‚Üí        console.log(chalk.red(`\n‚úó Unknown framework '${framework}'\n`));
   264‚Üí        console.log(chalk.white('List available frameworks:\n'));
   265‚Üí        console.log(chalk.cyan(`  codemie-${this.adapter.name} init --list\n`));
   266‚Üí        process.exit(1);
   267‚Üí      }
   268‚Üí
   269‚Üí      // Check if agent is supported by framework
   270‚Üí      const agentMapping = frameworkAdapter.getAgentMapping(this.adapter.name);
   271‚Üí      if (!agentMapping) {
   272‚Üí        console.log(chalk.red(`\n‚úó Framework '${framework}' does not support agent '${this.adapter.name}'\n`));
   273‚Üí        const supportedAgents = frameworkAdapter.metadata.supportedAgents || [];
   274‚Üí        if (supportedAgents.length > 0) {
   275‚Üí          console.log(chalk.white(`Supported agents: ${supportedAgents.join(', ')}\n`));
   276‚Üí        }
   277‚Üí        process.exit(1);
   278‚Üí      }
   279‚Üí
   280‚Üí      // Initialize framework
   281‚Üí      await frameworkAdapter.init(this.adapter.name, {
   282‚Üí        force: options.force as boolean | undefined,
   283‚Üí        projectName: options.projectName as string | undefined,
   284‚Üí        cwd: process.cwd()
   285‚Üí      });
   286‚Üí
   287‚Üí    } catch (error) {
   288‚Üí      const errorMessage = error instanceof Error ? error.message : String(error);
   289‚Üí      console.error(chalk.red(`\n‚úó Framework initialization failed\n`));
   290‚Üí      console.error(chalk.white(errorMessage));
   291‚Üí      console.error('');
   292‚Üí      logger.error('Framework initialization failed:', error);
   293‚Üí      process.exit(1);
   294‚Üí    }
   295‚Üí  }
   296‚Üí
   297‚Üí
   298‚Üí  /**
   299‚Üí   * Collect pass-through arguments from Commander options
   300‚Üí   */
   301‚Üí  private collectPassThroughArgs(
   302‚Üí    args: string[],
   303‚Üí    options: Record<string, unknown>
   304‚Üí  ): string[] {
   305‚Üí    const agentArgs = [...args];
   306‚Üí    // Config-only options (not passed to agent, handled by CodeMie CLI)
   307‚Üí    const configOnlyOptions = ['profile', 'provider', 'apiKey', 'baseUrl', 'timeout'];
   308‚Üí
   309‚Üí    for (const [key, value] of Object.entries(options)) {
   310‚Üí      // Skip config-only options (handled by CodeMie CLI layer)
   311‚Üí      if (configOnlyOptions.includes(key)) continue;
   312‚Üí
   313‚Üí      // Build flag key (--task or -t)
   314‚Üí      const flagKey = key.length === 1 ? `-${key}` : `--${key}`;
   315‚Üí
   316‚Üí      // Include all remaining options:
   317‚Üí      // 1. Options with flagMappings (will be transformed in BaseAgentAdapter)
   318‚Üí      // 2. Unknown options (allowUnknownOption enabled, pass through as-is)
   319‚Üí      agentArgs.push(flagKey);
   320‚Üí
   321‚Üí      if (value !== true && value !== undefined) {
   322‚Üí        agentArgs.push(String(value));
   323‚Üí      }
   324‚Üí    }
   325‚Üí
   326‚Üí    return agentArgs;
   327‚Üí  }
   328‚Üí
   329‚Üí  /**
   330‚Üí   * Get agent metadata (single source of truth)
   331‚Üí   */
   332‚Üí  private getAgentMetadata() {
   333‚Üí    const metadataMap: Record<string, typeof ClaudePluginMetadata> = {
   334‚Üí      'claude': ClaudePluginMetadata,
   335‚Üí      'codex': CodexPluginMetadata,
   336‚Üí      [BUILTIN_AGENT_NAME]: CodeMieCodePluginMetadata,
   337‚Üí      'gemini': GeminiPluginMetadata,
   338‚Üí      'deepagents': DeepAgentsPluginMetadata
   339‚Üí    };
   340‚Üí    return metadataMap[this.adapter.name];
   341‚Üí  }
   342‚Üí
   343‚Üí  /**
   344‚Üí   * Validate provider and model compatibility
   345‚Üí   */
   346‚Üí  private validateCompatibility(config: CodeMieConfigOptions): boolean {
   347‚Üí    const metadata = this.getAgentMetadata();
   348‚Üí    if (!metadata) {
   349‚Üí      logger.error(`Unknown agent '${this.adapter.name}'`);
   350‚Üí      return false;
   351‚Üí    }
   352‚Üí
   353‚Üí    const provider = config.provider || 'unknown';
   354‚Üí    const model = config.model || 'unknown';
   355‚Üí
   356‚Üí    // Check provider compatibility
   357‚Üí    if (!metadata.supportedProviders.includes(provider)) {
   358‚Üí      logger.error(`Provider '${provider}' is not supported by ${this.adapter.displayName}`);
   359‚Üí      console.log(chalk.white(`\nSupported providers: ${metadata.supportedProviders.join(', ')}`));
   360‚Üí      console.log(chalk.white('\nOptions:'));
   361‚Üí      console.log(chalk.white('  1. Run setup to choose a different provider: ') + chalk.cyan('codemie setup'));
   362‚Üí      return false;
   363‚Üí    }
   364‚Üí
   365‚Üí    // Check model compatibility
   366‚Üí    const blockedPatterns = metadata.blockedModelPatterns || [];
   367‚Üí    const isBlocked = blockedPatterns.some(pattern => pattern.test(model));
   368‚Üí
   369‚Üí    if (isBlocked) {
   370‚Üí      logger.error(`Model '${model}' is not compatible with ${this.adapter.displayName}`);
   371‚Üí      console.log(chalk.white('\nOptions:'));
   372‚Üí
   373‚Üí      // Get recommended models from agent metadata
   374‚Üí      const recommendedModels = metadata.recommendedModels;
   375‚Üí
   376‚Üí      if (recommendedModels && recommendedModels.length > 0) {
   377‚Üí        const modelExamples = recommendedModels.slice(0, 3).join(', ');
   378‚Üí        const suggestedModel = recommendedModels[0];
   379‚Üí        const command = this.adapter.name.startsWith('codemie-') ? this.adapter.name : `codemie-${this.adapter.name}`;
   380‚Üí
   381‚Üí        console.log(chalk.white(`  1. ${this.adapter.displayName} requires compatible models (e.g., ${modelExamples})`));
   382‚Üí        console.log(chalk.white('  2. Update profile: ') + chalk.cyan('codemie setup'));
   383‚Üí        console.log(chalk.white(`  3. Override for this session: ${command} --model ${suggestedModel}`));
   384‚Üí      } else {
   385‚Üí        console.log(chalk.white('  1. Update profile: ') + chalk.cyan('codemie setup'));
   386‚Üí      }
   387‚Üí
   388‚Üí      return false;
   389‚Üí    }
   390‚Üí
   391‚Üí    return true;
   392‚Üí  }
