     1‚Üí/**
     2‚Üí * Metrics Orchestrator
     3‚Üí *
     4‚Üí * Coordinates metrics collection across agent lifecycle:
     5‚Üí * 1. Pre-spawn snapshot
     6‚Üí * 2. Post-spawn snapshot + correlation
     7‚Üí * 3. Session creation and persistence
     8‚Üí *
     9‚Üí * Phase 1 & 2 implementation (Phase 3-5 later)
    10‚Üí */
    11‚Üí
    12‚Üíimport { randomUUID } from 'crypto';
    13‚Üíimport { FileSnapshotter } from './core/FileSnapshotter.js';
    14‚Üíimport { SessionCorrelator } from './core/SessionCorrelator.js';
    15‚Üíimport { SessionStore } from './session/SessionStore.js';
    16‚Üíimport { DeltaWriter } from './core/DeltaWriter.js';
    17‚Üíimport { SyncStateManager } from './core/SyncStateManager.js';
    18‚Üíimport type { AgentMetricsSupport, MetricsSession, FileSnapshot } from './types.js';
    19‚Üíimport { METRICS_CONFIG } from '../metrics-config.js';
    20‚Üíimport { logger } from '../../../utils/logger.js';
    21‚Üíimport { watch } from 'fs';
    22‚Üíimport { detectGitBranch } from '../../../utils/git.js';
    23‚Üíimport { createErrorContext, formatErrorForLog } from '../../../utils/error-context.js';
    24‚Üí
    25‚Üíexport interface MetricsOrchestratorOptions {
    26‚Üí  sessionId?: string; // Optional: provide existing session ID
    27‚Üí  agentName: string;
    28‚Üí  provider: string;
    29‚Üí  project?: string; // SSO project name (optional, only for ai-run-sso provider)
    30‚Üí  workingDirectory: string;
    31‚Üí  metricsAdapter: AgentMetricsSupport;
    32‚Üí}
    33‚Üí
    34‚Üíexport class MetricsOrchestrator {
    35‚Üí  private sessionId: string;
    36‚Üí  private agentName: string;
    37‚Üí  private provider: string;
    38‚Üí  private project?: string;
    39‚Üí  private workingDirectory: string;
    40‚Üí  private metricsAdapter: AgentMetricsSupport;
    41‚Üí
    42‚Üí  private snapshotter: FileSnapshotter;
    43‚Üí  private correlator: SessionCorrelator;
    44‚Üí  private store: SessionStore;
    45‚Üí
    46‚Üí  // Delta-based components
    47‚Üí  private deltaWriter: DeltaWriter | null = null;
    48‚Üí  private syncStateManager: SyncStateManager | null = null;
    49‚Üí  private fileWatcher: ReturnType<typeof watch> | null = null;
    50‚Üí
    51‚Üí  private beforeSnapshot: FileSnapshot | null = null;
    52‚Üí  private session: MetricsSession | null = null;
    53‚Üí  private isCollecting: boolean = false;
    54‚Üí
    55‚Üí  constructor(options: MetricsOrchestratorOptions) {
    56‚Üí    this.sessionId = options.sessionId || randomUUID();
    57‚Üí    this.agentName = options.agentName;
    58‚Üí    this.provider = options.provider;
    59‚Üí    this.project = options.project;
    60‚Üí    this.workingDirectory = options.workingDirectory;
    61‚Üí    this.metricsAdapter = options.metricsAdapter;
    62‚Üí
    63‚Üí    this.snapshotter = new FileSnapshotter();
    64‚Üí    this.correlator = new SessionCorrelator();
    65‚Üí    this.store = new SessionStore();
    66‚Üí  }
    67‚Üí
    68‚Üí  /**
    69‚Üí   * Check if metrics collection is enabled for this provider
    70‚Üí   */
    71‚Üí  isEnabled(): boolean {
    72‚Üí    return METRICS_CONFIG.enabled(this.provider);
    73‚Üí  }
    74‚Üí
    75‚Üí  /**
    76‚Üí   * Step 1: Take snapshot before agent spawn
    77‚Üí   * Called before spawning the agent process
    78‚Üí   * Note: This method is only called when metrics are enabled
    79‚Üí   */
    80‚Üí  async beforeAgentSpawn(): Promise<void> {
    81‚Üí    try {
    82‚Üí      logger.info('[MetricsOrchestrator] Preparing to track session metrics...');
    83‚Üí
    84‚Üí      // Get agent data paths
    85‚Üí      const { sessionsDir } = this.metricsAdapter.getDataPaths();
    86‚Üí      logger.debug(`[MetricsOrchestrator] Taking pre-spawn snapshot of: ${sessionsDir}`);
    87‚Üí
    88‚Üí      // Take snapshot
    89‚Üí      this.beforeSnapshot = await this.snapshotter.snapshot(sessionsDir);
    90‚Üí
    91‚Üí      logger.info(`[MetricsOrchestrator] Baseline: ${this.beforeSnapshot.files.length} existing session file${this.beforeSnapshot.files.length !== 1 ? 's' : ''}`);
    92‚Üí      logger.debug(`[MetricsOrchestrator] Pre-spawn snapshot complete: ${this.beforeSnapshot.files.length} files`);
    93‚Üí
    94‚Üí      // Show sample of baseline files for debugging
    95‚Üí      if (this.beforeSnapshot.files.length > 0) {
    96‚Üí        const sampleSize = Math.min(3, this.beforeSnapshot.files.length);
    97‚Üí        const sample = this.beforeSnapshot.files.slice(0, sampleSize).map(f => f.path);
    98‚Üí        logger.info(`[MetricsOrchestrator] Sample files (first ${sampleSize}):`);
    99‚Üí        for (const filePath of sample) {
   100‚Üí          logger.info(`[MetricsOrchestrator]    ‚Üí ${filePath}`);
   101‚Üí        }
   102‚Üí        if (this.beforeSnapshot.files.length > sampleSize) {
   103‚Üí          logger.info(`[MetricsOrchestrator]    ... and ${this.beforeSnapshot.files.length - sampleSize} more`);
   104‚Üí        }
   105‚Üí      }
   106‚Üí
   107‚Üí      // Detect git branch from working directory
   108‚Üí      const gitBranch = await detectGitBranch(this.workingDirectory);
   109‚Üí
   110‚Üí      // Create session record
   111‚Üí      this.session = {
   112‚Üí        sessionId: this.sessionId,
   113‚Üí        agentName: this.agentName,
   114‚Üí        provider: this.provider,
   115‚Üí        ...(this.project && { project: this.project }),
   116‚Üí        startTime: Date.now(),
   117‚Üí        workingDirectory: this.workingDirectory,
   118‚Üí        ...(gitBranch && { gitBranch }), // Include branch if detected
   119‚Üí        status: 'active',
   120‚Üí        correlation: {
   121‚Üí          status: 'pending',
   122‚Üí          retryCount: 0
   123‚Üí        },
   124‚Üí        monitoring: {
   125‚Üí          isActive: false,
   126‚Üí          changeCount: 0
   127‚Üí        }
   128‚Üí      };
   129‚Üí
   130‚Üí      // Save initial session
   131‚Üí      await this.store.saveSession(this.session);
   132‚Üí      logger.info(`[MetricsOrchestrator] Session created: ${this.sessionId}`);
   133‚Üí      logger.debug(`[MetricsOrchestrator] Agent: ${this.agentName}, Provider: ${this.provider}`);
   134‚Üí
   135‚Üí    } catch (error) {
   136‚Üí      // Disable metrics for the rest of the session to prevent log pollution
   137‚Üí      process.env.CODEMIE_METRICS_DISABLED = '1';
   138‚Üí
   139‚Üí      // Create comprehensive error context for logging
   140‚Üí      const errorContext = createErrorContext(error, {
   141‚Üí        sessionId: this.sessionId,
   142‚Üí        agent: this.agentName,
   143‚Üí        provider: this.provider,
   144‚Üí        ...(this.project && { model: this.project })
   145‚Üí      });
   146‚Üí
   147‚Üí      logger.error(
   148‚Üí        '[MetricsOrchestrator] Failed to take pre-spawn snapshot',
   149‚Üí        formatErrorForLog(errorContext)
   150‚Üí      );
   151‚Üí
   152‚Üí      // Store raw error for display to user (not ErrorContext)
   153‚Üí      if (this.session) {
   154‚Üí        (this.session as any).initError = error;
   155‚Üí      }
   156‚Üí
   157‚Üí      // Don't throw - metrics failures shouldn't break agent execution
   158‚Üí    }
   159‚Üí  }
   160‚Üí
   161‚Üí  /**
   162‚Üí   * Step 2: Take snapshot after agent spawn + correlate
   163‚Üí   * Called after spawning the agent process
   164‚Üí   * Note: This method is only called when metrics are enabled
   165‚Üí   */
   166‚Üí  async afterAgentSpawn(): Promise<void> {
   167‚Üí    if (!this.isEnabled() || !this.beforeSnapshot || !this.session) {
   168‚Üí      return;
   169‚Üí    }
   170‚Üí
   171‚Üí    try {
   172‚Üí      logger.info(`[MetricsOrchestrator] Agent started - waiting for session file creation...`);
   173‚Üí
   174‚Üí      // Wait for agent to initialize and create session file
   175‚Üí      const initDelay = this.metricsAdapter.getInitDelay();
   176‚Üí      await this.sleep(initDelay);
   177‚Üí
   178‚Üí      // Get agent data paths
   179‚Üí      const { sessionsDir } = this.metricsAdapter.getDataPaths();
   180‚Üí      logger.info(`[MetricsOrchestrator] Scanning directory: ${sessionsDir}`);
   181‚Üí
   182‚Üí      // Take snapshot
   183‚Üí      const afterSnapshot = await this.snapshotter.snapshot(sessionsDir);
   184‚Üí      logger.info(`[MetricsOrchestrator] Found ${afterSnapshot.files.length} total session file${afterSnapshot.files.length !== 1 ? 's' : ''} in directory`);
   185‚Üí      logger.debug(`[MetricsOrchestrator] Pre-spawn: ${this.beforeSnapshot.files.length} files, Post-spawn: ${afterSnapshot.files.length} files`);
   186‚Üí
   187‚Üí      // Show sample of post-spawn files for comparison
   188‚Üí      if (afterSnapshot.files.length > 0 && afterSnapshot.files.length !== this.beforeSnapshot.files.length) {
   189‚Üí        const sampleSize = Math.min(3, afterSnapshot.files.length);
   190‚Üí        const sample = afterSnapshot.files.slice(0, sampleSize).map(f => f.path);
   191‚Üí        logger.info(`[MetricsOrchestrator] Post-spawn files (first ${sampleSize}):`);
   192‚Üí        for (const filePath of sample) {
   193‚Üí          logger.info(`[MetricsOrchestrator]    ‚Üí ${filePath}`);
   194‚Üí        }
   195‚Üí        if (afterSnapshot.files.length > sampleSize) {
   196‚Üí          logger.info(`[MetricsOrchestrator]    ... and ${afterSnapshot.files.length - sampleSize} more`);
   197‚Üí        }
   198‚Üí      }
   199‚Üí
   200‚Üí      // Compute diff
   201‚Üí      const newFiles = this.snapshotter.diff(this.beforeSnapshot, afterSnapshot);
   202‚Üí      if (newFiles.length > 0) {
   203‚Üí        logger.info(`[MetricsOrchestrator] ${newFiles.length} new file${newFiles.length !== 1 ? 's' : ''} created since agent start`);
   204‚Üí        // Use path.basename for cross-platform display
   205‚Üí        const { basename } = await import('path');
   206‚Üí        logger.info(`[MetricsOrchestrator]    ${newFiles.map(f => `‚Üí ${basename(f.path)}`).join(', ')}`);
   207‚Üí        logger.debug(`[MetricsOrchestrator] New files (full paths): ${newFiles.map(f => f.path).join(', ')}`);
   208‚Üí      } else {
   209‚Üí        logger.info(`[MetricsOrchestrator] No new files yet - will retry...`);
   210‚Üí        logger.debug(`[MetricsOrchestrator] Diff result: 0 new files (baseline had ${this.beforeSnapshot.files.length}, post-spawn has ${afterSnapshot.files.length})`);
   211‚Üí      }
   212‚Üí
   213‚Üí      // Correlate with retry
   214‚Üí      logger.debug('[MetricsOrchestrator] Starting correlation with retry...');
   215‚Üí      const correlation = await this.correlator.correlateWithRetry(
   216‚Üí        {
   217‚Üí          sessionId: this.sessionId,
   218‚Üí          agentName: this.agentName,
   219‚Üí          workingDirectory: this.workingDirectory,
   220‚Üí          newFiles,
   221‚Üí          agentPlugin: this.metricsAdapter
   222‚Üí        },
   223‚Üí        async () => {
   224‚Üí          // Snapshot function for retries
   225‚Üí          const retrySnapshot = await this.snapshotter.snapshot(sessionsDir);
   226‚Üí          return this.snapshotter.diff(this.beforeSnapshot!, retrySnapshot);
   227‚Üí        }
   228‚Üí      );
   229‚Üí
   230‚Üí      // Update session with correlation result
   231‚Üí      await this.store.updateSessionCorrelation(this.sessionId, correlation);
   232‚Üí
   233‚Üí      // Reload session to get updated correlation
   234‚Üí      this.session = await this.store.loadSession(this.sessionId);
   235‚Üí
   236‚Üí      if (correlation.status === 'matched') {
   237‚Üí        logger.debug(`[MetricsOrchestrator] Session correlated: ${correlation.agentSessionId}`);
   238‚Üí        logger.debug(`[MetricsOrchestrator]   Agent file: ${correlation.agentSessionFile}`);
   239‚Üí        logger.debug(`[MetricsOrchestrator]   Retry count: ${correlation.retryCount}`);
   240‚Üí
   241‚Üí        // Start incremental delta monitoring
   242‚Üí        await this.startIncrementalMonitoring(correlation.agentSessionFile!);
   243‚Üí      } else {
   244‚Üí        logger.warn(`[MetricsOrchestrator] Correlation failed after ${correlation.retryCount} retries`);
   245‚Üí      }
   246‚Üí
   247‚Üí    } catch (error) {
   248‚Üí      // Create comprehensive error context for logging
   249‚Üí      const errorContext = createErrorContext(error, {
   250‚Üí        sessionId: this.sessionId,
   251‚Üí        agent: this.agentName,
   252‚Üí        provider: this.provider,
   253‚Üí        ...(this.project && { model: this.project })
   254‚Üí      });
   255‚Üí
   256‚Üí      logger.error(
   257‚Üí        '[MetricsOrchestrator] Failed in post-spawn phase',
   258‚Üí        formatErrorForLog(errorContext)
   259‚Üí      );
   260‚Üí
   261‚Üí      // Store raw error for display to user (not ErrorContext)
   262‚Üí      if (this.session) {
   263‚Üí        (this.session as any).postSpawnError = error;
   264‚Üí      }
   265‚Üí
   266‚Üí      // Don't throw - metrics failures shouldn't break agent execution
   267‚Üí    }
   268‚Üí  }
   269‚Üí
   270‚Üí  /**
   271‚Üí   * Finalize session on agent exit
   272‚Üí   * Called when agent process exits
   273‚Üí   * Note: This method is only called when metrics are enabled
   274‚Üí   */
   275‚Üí  async onAgentExit(exitCode: number): Promise<void> {
   276‚Üí    if (!this.isEnabled() || !this.session) {
   277‚Üí      return;
   278‚Üí    }
   279‚Üí
   280‚Üí    try {
   281‚Üí      logger.debug('[MetricsOrchestrator] Finalizing session...');
   282‚Üí
   283‚Üí      // Stop file watcher
   284‚Üí      if (this.fileWatcher) {
   285‚Üí        this.fileWatcher.close();
   286‚Üí        this.fileWatcher = null;
   287‚Üí        logger.debug('[MetricsOrchestrator] Stopped file watcher');
   288‚Üí      }
   289‚Üí
   290‚Üí      // Collect final deltas
   291‚Üí      if (this.session.correlation.status === 'matched' &&
   292‚Üí          this.session.correlation.agentSessionFile) {
   293‚Üí        await this.collectDeltas(this.session.correlation.agentSessionFile);
   294‚Üí        logger.debug('[MetricsOrchestrator] Collected final deltas');
   295‚Üí      }
   296‚Üí
   297‚Üí      // Update sync state status with end time
   298‚Üí      const endTime = Date.now();
   299‚Üí      const status = exitCode === 0 ? 'completed' : 'failed';
   300‚Üí
   301‚Üí      if (this.syncStateManager) {
   302‚Üí        await this.syncStateManager.updateStatus(status, endTime);
   303‚Üí      }
   304‚Üí
   305‚Üí      // Update session status
   306‚Üí      await this.store.updateSessionStatus(this.sessionId, status);
   307‚Üí
   308‚Üí      logger.debug('[MetricsOrchestrator] Session finalized');
   309‚Üí
   310‚Üí    } catch (error) {
   311‚Üí      // Create comprehensive error context for logging
   312‚Üí      const errorContext = createErrorContext(error, {
   313‚Üí        sessionId: this.sessionId,
   314‚Üí        agent: this.agentName,
   315‚Üí        provider: this.provider,
   316‚Üí        ...(this.project && { model: this.project })
   317‚Üí      });
   318‚Üí
   319‚Üí      logger.error(
   320‚Üí        '[MetricsOrchestrator] Failed to finalize session',
   321‚Üí        formatErrorForLog(errorContext)
   322‚Üí      );
   323‚Üí
   324‚Üí      // Don't throw - metrics failures shouldn't break agent execution
   325‚Üí    }
   326‚Üí  }
   327‚Üí
   328‚Üí  /**
   329‚Üí   * Start incremental monitoring with delta collection
   330‚Üí   */
   331‚Üí  private async startIncrementalMonitoring(sessionFilePath: string): Promise<void> {
   332‚Üí    if (!this.isEnabled() || !this.session || !this.session.correlation.agentSessionId) {
   333‚Üí      return;
   334‚Üí    }
   335‚Üí
   336‚Üí    try {
   337‚Üí      // Initialize delta writer and sync state manager
   338‚Üí      this.deltaWriter = new DeltaWriter(this.sessionId);
   339‚Üí      this.syncStateManager = new SyncStateManager(this.sessionId);
   340‚Üí
   341‚Üí      // Initialize sync state with session start time
   342‚Üí      await this.syncStateManager.initialize(
   343‚Üí        this.sessionId,
   344‚Üí        this.session.correlation.agentSessionId,
   345‚Üí        this.session.startTime
   346‚Üí      );
   347‚Üí
   348‚Üí      logger.info('[MetricsOrchestrator] Monitoring session activity in real-time');
   349‚Üí      logger.debug('[MetricsOrchestrator] Initialized delta-based metrics tracking');
   350‚Üí
   351‚Üí      // Collect initial deltas
   352‚Üí      await this.collectDeltas(sessionFilePath);
   353‚Üí
   354‚Üí      // Start file watching
   355‚Üí      let debounceTimer: NodeJS.Timeout | null = null;
   356‚Üí      const DEBOUNCE_DELAY = 5000; // 5 seconds
   357‚Üí
   358‚Üí      this.fileWatcher = watch(sessionFilePath, (eventType) => {
   359‚Üí        if (eventType === 'change') {
   360‚Üí          // Debounce: wait 5s after last change before collecting
   361‚Üí          if (debounceTimer) {
   362‚Üí            clearTimeout(debounceTimer);
   363‚Üí          }
   364‚Üí
   365‚Üí          debounceTimer = setTimeout(async () => {
   366‚Üí            await this.collectDeltas(sessionFilePath);
   367‚Üí          }, DEBOUNCE_DELAY);
   368‚Üí        }
   369‚Üí      });
   370‚Üí
   371‚Üí      logger.debug('[MetricsOrchestrator] Started file watching for incremental metrics');
   372‚Üí
   373‚Üí    } catch (error) {
   374‚Üí      logger.error('[MetricsOrchestrator] Failed to start incremental monitoring:', error);
   375‚Üí    }
   376‚Üí  }
   377‚Üí
   378‚Üí  /**
   379‚Üí   * Collect delta metrics from agent session file
   380‚Üí   */
   381‚Üí  private async collectDeltas(sessionFilePath: string): Promise<void> {
   382‚Üí    // Prevent concurrent collection or if metrics disabled
   383‚Üí    if (!this.isEnabled() || this.isCollecting || !this.deltaWriter || !this.syncStateManager) {
   384‚Üí      return;
   385‚Üí    }
   386‚Üí
   387‚Üí    this.isCollecting = true;
   388‚Üí
   389‚Üí    try {
   390‚Üí      // Load current sync state
   391‚Üí      const syncState = await this.syncStateManager.load();
   392‚Üí
   393‚Üí      // If sync state doesn't exist (file deleted or not initialized yet), skip collection
   394‚Üí      if (!syncState) {
   395‚Üí        logger.debug('[MetricsOrchestrator] Sync state not available, skipping delta collection');
   396‚Üí        this.isCollecting = false;
   397‚Üí        return;
   398‚Üí      }
   399‚Üí
   400‚Üí      // Get already-processed record IDs from sync state
   401‚Üí      const processedRecordIds = new Set(syncState.processedRecordIds);
   402‚Üí
   403‚Üí      // Get already-attached user prompt texts from sync state
   404‚Üí      const attachedUserPromptTexts = new Set(syncState.attachedUserPromptTexts || []);
   405‚Üí
   406‚Üí      // Parse incremental metrics with processed record IDs and attached prompts
   407‚Üí      logger.info(`[MetricsOrchestrator] Scanning session for new activity...`);
   408‚Üí      const { deltas, lastLine, newlyAttachedPrompts } = await this.metricsAdapter.parseIncrementalMetrics(
   409‚Üí        sessionFilePath,
   410‚Üí        processedRecordIds,
   411‚Üí        attachedUserPromptTexts
   412‚Üí      );
   413‚Üí
   414‚Üí      if (deltas.length === 0) {
   415‚Üí        logger.debug('[MetricsOrchestrator] No new deltas to collect');
   416‚Üí        this.isCollecting = false;
   417‚Üí        return;
   418‚Üí      }
   419‚Üí
   420‚Üí      logger.info(`[MetricsOrchestrator] Found ${deltas.length} new interaction${deltas.length !== 1 ? 's' : ''} to record`);
   421‚Üí
   422‚Üí      // Collect record IDs for tracking
   423‚Üí      const newRecordIds: string[] = [];
   424‚Üí
   425‚Üí      // Calculate summary statistics for logging
   426‚Üí      let totalTokens = 0;
   427‚Üí      let totalTools = 0;
   428‚Üí      let totalFiles = 0;
   429‚Üí
   430‚Üí      // Append each delta to JSONL
   431‚Üí      for (const delta of deltas) {
   432‚Üí        // Set CodeMie session ID
   433‚Üí        delta.sessionId = this.sessionId;
   434‚Üí
   435‚Üí        // Set gitBranch if not already present in delta
   436‚Üí        if (!delta.gitBranch) {
   437‚Üí          delta.gitBranch = await detectGitBranch(this.workingDirectory);
   438‚Üí        }
   439‚Üí
   440‚Üí        // Accumulate statistics
   441‚Üí        if (delta.tokens) {
   442‚Üí          totalTokens += (delta.tokens.input || 0) + (delta.tokens.output || 0);
   443‚Üí        }
   444‚Üí        if (delta.tools) {
   445‚Üí          totalTools += Object.values(delta.tools).reduce((sum, count) => sum + count, 0);
   446‚Üí        }
   447‚Üí        if (delta.fileOperations) {
   448‚Üí          totalFiles += delta.fileOperations.length;
   449‚Üí        }
   450‚Üí
   451‚Üí        // Append to JSONL
   452‚Üí        await this.deltaWriter.appendDelta(delta);
   453‚Üí        newRecordIds.push(delta.recordId);
   454‚Üí      }
   455‚Üí
   456‚Üí      // Update sync state with processed record IDs
   457‚Üí      await this.syncStateManager.addProcessedRecords(newRecordIds);
   458‚Üí      await this.syncStateManager.updateLastProcessed(lastLine, Date.now());
   459‚Üí      await this.syncStateManager.incrementDeltas(deltas.length);
   460‚Üí
   461‚Üí      // Update sync state with newly attached user prompts
   462‚Üí      if (newlyAttachedPrompts && newlyAttachedPrompts.length > 0) {
   463‚Üí        await this.syncStateManager.addAttachedUserPrompts(newlyAttachedPrompts);
   464‚Üí      }
   465‚Üí
   466‚Üí      // Log summary with meaningful statistics
   467‚Üí      const parts: string[] = [];
   468‚Üí      if (totalTokens > 0) parts.push(`${totalTokens.toLocaleString()} tokens`);
   469‚Üí      if (totalTools > 0) parts.push(`${totalTools} tool${totalTools !== 1 ? 's' : ''}`);
   470‚Üí      if (totalFiles > 0) parts.push(`${totalFiles} file${totalFiles !== 1 ? 's' : ''}`);
   471‚Üí
   472‚Üí      const summary = parts.length > 0 ? ` (${parts.join(', ')})` : '';
   473‚Üí      logger.info(`[MetricsOrchestrator] Recorded${summary}`);
   474‚Üí      logger.debug(`[MetricsOrchestrator] Processed up to line ${lastLine}`);
   475‚Üí
   476‚Üí    } catch (error) {
   477‚Üí      // Create comprehensive error context for logging
   478‚Üí      const errorContext = createErrorContext(error, {
   479‚Üí        sessionId: this.sessionId,
   480‚Üí        agent: this.agentName,
   481‚Üí        provider: this.provider,
   482‚Üí        ...(this.project && { model: this.project })
   483‚Üí      });
   484‚Üí
   485‚Üí      logger.error(
   486‚Üí        '[MetricsOrchestrator] Failed to collect deltas',
   487‚Üí        formatErrorForLog(errorContext)
   488‚Üí      );
   489‚Üí    } finally {
   490‚Üí      this.isCollecting = false;
   491‚Üí    }
   492‚Üí  }
   493‚Üí
   494‚Üí  /**
   495‚Üí   * Get initialization errors for display to user
   496‚Üí   * Returns the first error that occurred during metrics initialization
   497‚Üí   */
   498‚Üí  getInitializationError(): unknown | null {
   499‚Üí    if (!this.session) {
   500‚Üí      return null;
   501‚Üí    }
   502‚Üí
   503‚Üí    // Check for errors in order of occurrence
   504‚Üí    const sessionWithErrors = this.session as any;
   505‚Üí    return sessionWithErrors.initError || sessionWithErrors.postSpawnError || null;
   506‚Üí  }
   507‚Üí
   508‚Üí  /**
   509‚Üí   * Check if metrics initialization had any errors
   510‚Üí   */
   511‚Üí  hasInitializationError(): boolean {
   512‚Üí    return this.getInitializationError() !== null;
   513‚Üí  }
   514‚Üí
   515‚Üí  /**
   516‚Üí   * Sleep utility
   517‚Üí   */
   518‚Üí  private sleep(ms: number): Promise<void> {
   519‚Üí    return new Promise(resolve => setTimeout(resolve, ms));
   520‚Üí  }
   521‚Üí}
