    74‚Üí    }
    75‚Üí
    76‚Üí    try {
    77‚Üí      await npm.installGlobal(this.metadata.npmPackage);
    78‚Üí    } catch (error: unknown) {
    79‚Üí      if (error instanceof NpmError) {
    80‚Üí        throw new Error(`Failed to install ${this.displayName}: ${error.message}`);
    81‚Üí      }
    82‚Üí      throw error;
    83‚Üí    }
    84‚Üí  }
    85‚Üí
    86‚Üí  /**
    87‚Üí   * Uninstall agent via npm
    88‚Üí   */
    89‚Üí  async uninstall(): Promise<void> {
    90‚Üí    if (!this.metadata.npmPackage) {
    91‚Üí      throw new Error(`${this.displayName} is built-in and cannot be uninstalled`);
    92‚Üí    }
    93‚Üí
    94‚Üí    try {
    95‚Üí      await npm.uninstallGlobal(this.metadata.npmPackage);
    96‚Üí    } catch (error: unknown) {
    97‚Üí      if (error instanceof NpmError) {
    98‚Üí        throw new Error(`Failed to uninstall ${this.displayName}: ${error.message}`);
    99‚Üí      }
   100‚Üí      throw error;
   101‚Üí    }
   102‚Üí  }
