   104‚Üí  /**
   105‚Üí   * Check if agent is installed (cross-platform)
   106‚Üí   */
   107‚Üí  async isInstalled(): Promise<boolean> {
   108‚Üí    if (!this.metadata.cliCommand) {
   109‚Üí      return true; // Built-in agents are always "installed"
   110‚Üí    }
   111‚Üí
   112‚Üí    try {
   113‚Üí      // Use commandExists which handles Windows (where) vs Unix (which)
   114‚Üí      const { commandExists } = await import('../../utils/which.js');
   115‚Üí      return await commandExists(this.metadata.cliCommand);
   116‚Üí    } catch {
   117‚Üí      return false;
   118‚Üí    }
   119‚Üí  }
