   121‚Üí  /**
   122‚Üí   * Get agent version
   123‚Üí   */
   124‚Üí  async getVersion(): Promise<string | null> {
   125‚Üí    if (!this.metadata.cliCommand) {
   126‚Üí      return null;
   127‚Üí    }
   128‚Üí
   129‚Üí    try {
   130‚Üí      const result = await exec(this.metadata.cliCommand, ['--version']);
   131‚Üí      return result.stdout.trim();
   132‚Üí    } catch {
   133‚Üí      return null;
