   136‚Üí
   137‚Üí  /**
   138‚Üí   * Run the agent
   139‚Üí   */
   140‚Üí  async run(args: string[], envOverrides?: Record<string, string>): Promise<void> {
   141‚Üí    // Generate session ID at the very start - this is the source of truth
   142‚Üí    // All components (logger, metrics, proxy) will use this same session ID
   143‚Üí    const sessionId = randomUUID();
   144‚Üí
   145‚Üí    // Merge environment variables
   146‚Üí    let env: NodeJS.ProcessEnv = {
   147‚Üí      ...process.env,
   148‚Üí      ...envOverrides,
   149‚Üí      CODEMIE_SESSION_ID: sessionId,
   150‚Üí      CODEMIE_AGENT: this.metadata.name
   151‚Üí    };
   152‚Üí
   153‚Üí    // Initialize logger with session ID
   154‚Üí    const { logger } = await import('../../utils/logger.js');
   155‚Üí    logger.setSessionId(sessionId);
   156‚Üí
   157‚Üí    // Setup metrics orchestrator with the session ID
   158‚Üí    // Only create if metrics are enabled for the provider
   159‚Üí    const metricsAdapter = this.getMetricsAdapter();
   160‚Üí    if (metricsAdapter && env.CODEMIE_PROVIDER) {
   161‚Üí      const { METRICS_CONFIG } = await import('./metrics-config.js');
   162‚Üí
   163‚Üí      // Check if metrics are enabled for this provider before creating orchestrator
   164‚Üí      if (METRICS_CONFIG.enabled(env.CODEMIE_PROVIDER)) {
   165‚Üí        this.metricsOrchestrator = new MetricsOrchestrator({
   166‚Üí          agentName: this.metadata.name,
   167‚Üí          provider: env.CODEMIE_PROVIDER,
   168‚Üí          project: env.CODEMIE_PROJECT,
   169‚Üí          workingDirectory: process.cwd(),
   170‚Üí          metricsAdapter,
   171‚Üí          sessionId // Pass the session ID explicitly
   172‚Üí        });
   173‚Üí
   174‚Üí        // Take pre-spawn snapshot
   175‚Üí        await this.metricsOrchestrator.beforeAgentSpawn();
   176‚Üí      }
   177‚Üí    }
   178‚Üí
   179‚Üí    // Setup proxy with the session ID (already in env)
   180‚Üí    await this.setupProxy(env);
   181‚Üí
   182‚Üí    // Lifecycle hook: session start (provider-aware)
   183‚Üí    await executeOnSessionStart(this, this.metadata.lifecycle, this.metadata.name, sessionId, env);
   184‚Üí
   185‚Üí    // Show welcome message with session info
   186‚Üí    const profileName = env.CODEMIE_PROFILE_NAME || 'default';
   187‚Üí    const provider = env.CODEMIE_PROVIDER || 'unknown';
   188‚Üí    const cliVersion = env.CODEMIE_CLI_VERSION || 'unknown';
   189‚Üí    const model = env.CODEMIE_MODEL || 'unknown';
   190‚Üí    const codeMieUrl = env.CODEMIE_URL;
   191‚Üí
   192‚Üí    // Display ASCII logo with configuration
   193‚Üí    console.log(
   194‚Üí      renderProfileInfo({
   195‚Üí        profile: profileName,
   196‚Üí        provider,
   197‚Üí        model,
   198‚Üí        codeMieUrl,
   199‚Üí        agent: this.metadata.name,
   200‚Üí        cliVersion,
   201‚Üí        sessionId
   202‚Üí      })
   203‚Üí    );
   204‚Üí
   205‚Üí    // Show random welcome message
   206‚Üí    console.log(chalk.cyan.bold(getRandomWelcomeMessage()));
   207‚Üí    console.log(''); // Empty line for spacing
   208‚Üí
   209‚Üí    // Display metrics initialization errors (non-blocking)
   210‚Üí    if (this.metricsOrchestrator?.hasInitializationError()) {
   211‚Üí      const metricsError = this.metricsOrchestrator.getInitializationError();
   212‚Üí      if (metricsError) {
   213‚Üí        const { displayWarningMessage } = await import('../../utils/profile.js');
   214‚Üí        displayWarningMessage(
   215‚Üí          'Metrics Collection Disabled',
   216‚Üí          metricsError,
   217‚Üí          {
   218‚Üí            sessionId,
   219‚Üí            agent: this.metadata.name,
   220‚Üí            provider,
   221‚Üí            model,
   222‚Üí            profile: profileName
   223‚Üí          },
   224‚Üí          { severity: 'warning' }
   225‚Üí        );
   226‚Üí      }
   227‚Üí    }
   228‚Üí
   229‚Üí    // Transform CODEMIE_* ‚Üí agent-specific env vars (based on envMapping)
   230‚Üí    env = this.transformEnvVars(env);
   231‚Üí
   232‚Üí    // Lifecycle hook: beforeRun (provider-aware)
   233‚Üí    // Can override or extend env transformations, setup config files
   234‚Üí    env = await executeBeforeRun(this, this.metadata.lifecycle, this.metadata.name, env, this.extractConfig(env));
   235‚Üí
   236‚Üí    // Lifecycle hook: enrichArgs (provider-aware)
   237‚Üí    // Enrich args with agent-specific defaults (e.g., --profile, --model)
   238‚Üí    // Must run AFTER beforeRun so env vars like CODEMIE_CODEX_PROFILE are available
   239‚Üí    let enrichedArgs = executeEnrichArgs(this.metadata.lifecycle, this.metadata.name, args, this.extractConfig(env));
   240‚Üí
   241‚Üí    // Apply argument transformations using declarative flagMappings
   242‚Üí    let transformedArgs: string[];
   243‚Üí
   244‚Üí    if (this.metadata.flagMappings) {
   245‚Üí      const { transformFlags } = await import('./flag-transform.js');
   246‚Üí      transformedArgs = transformFlags(enrichedArgs, this.metadata.flagMappings, this.extractConfig(env));
   247‚Üí    } else {
   248‚Üí      transformedArgs = enrichedArgs;
   249‚Üí    }
   250‚Üí
   251‚Üí    // Log configuration (CODEMIE_* + transformed agent-specific vars)
   252‚Üí    logger.debug('=== Agent Configuration ===');
   253‚Üí    const codemieVars = Object.keys(env)
   254‚Üí      .filter(k => k.startsWith('CODEMIE_'))
   255‚Üí      .sort();
   256‚Üí
   257‚Üí    for (const key of codemieVars) {
   258‚Üí      const value = env[key];
   259‚Üí      if (value) {
   260‚Üí        if (key.includes('KEY') || key.includes('TOKEN')) {
   261‚Üí          const masked = value.length > 12
   262‚Üí            ? value.substring(0, 8) + '***' + value.substring(value.length - 4)
   263‚Üí            : '***';
   264‚Üí          logger.debug(`${key}: ${masked}`);
   265‚Üí        } else if (key === 'CODEMIE_PROFILE_CONFIG') {
   266‚Üí          logger.debug(`${key}: <config object>`);
   267‚Üí        } else {
   268‚Üí          logger.debug(`${key}: ${value}`);
   269‚Üí        }
   270‚Üí      }
   271‚Üí    }
   272‚Üí
   273‚Üí    if (this.metadata.envMapping) {
   274‚Üí      const agentVars = [
   275‚Üí        ...(this.metadata.envMapping.baseUrl || []),
   276‚Üí        ...(this.metadata.envMapping.apiKey || []),
   277‚Üí        ...(this.metadata.envMapping.model || [])
   278‚Üí      ].sort();
   279‚Üí
   280‚Üí      if (agentVars.length > 0) {
   281‚Üí        logger.debug('--- Agent-Specific Variables ---');
   282‚Üí        for (const key of agentVars) {
   283‚Üí          const value = env[key];
   284‚Üí          if (value) {
   285‚Üí            if (key.toLowerCase().includes('key') || key.toLowerCase().includes('token')) {
   286‚Üí              const masked = value.length > 12
   287‚Üí                ? value.substring(0, 8) + '***' + value.substring(value.length - 4)
   288‚Üí                : '***';
   289‚Üí              logger.debug(`${key}: ${masked}`);
   290‚Üí            } else {
   291‚Üí              logger.debug(`${key}: ${value}`);
   292‚Üí            }
   293‚Üí          }
   294‚Üí        }
   295‚Üí      }
   296‚Üí    }
   297‚Üí    logger.debug('=== End Configuration ===');
   298‚Üí
   299‚Üí    if (!this.metadata.cliCommand) {
   300‚Üí      throw new Error(`${this.displayName} has no CLI command configured`);
   301‚Üí    }
   302‚Üí
   303‚Üí    try {
   304‚Üí      // Log command execution
   305‚Üí      logger.debug(`Executing: ${this.metadata.cliCommand} ${transformedArgs.join(' ')}`);
   306‚Üí
   307‚Üí      // Spawn the CLI command with inherited stdio
   308‚Üí      // On Windows, resolve full path to avoid shell: true deprecation (DEP0190)
   309‚Üí      const isWindows = process.platform === 'win32';
   310‚Üí      let commandPath = this.metadata.cliCommand;
   311‚Üí
   312‚Üí      // Resolve full path on Windows to avoid using shell: true
   313‚Üí      if (isWindows) {
   314‚Üí        const { getCommandPath } = await import('../../utils/which.js');
   315‚Üí        const resolvedPath = await getCommandPath(this.metadata.cliCommand);
   316‚Üí        if (resolvedPath) {
   317‚Üí          commandPath = resolvedPath;
   318‚Üí          logger.debug(`Resolved command path: ${resolvedPath}`);
   319‚Üí        }
   320‚Üí      }
   321‚Üí
   322‚Üí      const child = spawn(commandPath, transformedArgs, {
   323‚Üí        stdio: 'inherit',
   324‚Üí        env,
   325‚Üí        windowsHide: isWindows // Hide console window on Windows
   326‚Üí      });
   327‚Üí
   328‚Üí      // Take post-spawn snapshot after process starts
   329‚Üí      if (this.metricsOrchestrator) {
   330‚Üí        // Don't await - let it run in background
   331‚Üí        this.metricsOrchestrator.afterAgentSpawn().catch(err => {
   332‚Üí          logger.error('[MetricsOrchestrator] Post-spawn snapshot failed:', err);
   333‚Üí        });
   334‚Üí      }
   335‚Üí
   336‚Üí      // Define cleanup function for proxy and metrics
   337‚Üí      const cleanup = async () => {
   338‚Üí        if (this.proxy) {
   339‚Üí          logger.debug(`[${this.displayName}] Stopping proxy and flushing analytics...`);
   340‚Üí          await this.proxy.stop();
   341‚Üí          this.proxy = null;
   342‚Üí          logger.debug(`[${this.displayName}] Proxy cleanup complete`);
   343‚Üí        }
   344‚Üí      };
   345‚Üí
   346‚Üí      // Signal handler for graceful shutdown
   347‚Üí      const handleSignal = async (signal: NodeJS.Signals) => {
   348‚Üí        logger.debug(`Received ${signal}, cleaning up proxy...`);
   349‚Üí        await cleanup();
   350‚Üí        // Kill child process gracefully
   351‚Üí        child.kill(signal);
   352‚Üí      };
   353‚Üí
   354‚Üí      // Register signal handlers
   355‚Üí      const sigintHandler = () => handleSignal('SIGINT');
   356‚Üí      const sigtermHandler = () => handleSignal('SIGTERM');
   357‚Üí
   358‚Üí      process.once('SIGINT', sigintHandler);
   359‚Üí      process.once('SIGTERM', sigtermHandler);
   360‚Üí
   361‚Üí      return new Promise((resolve, reject) => {
   362‚Üí        child.on('error', (error) => {
   363‚Üí          reject(new Error(`Failed to start ${this.displayName}: ${error.message}`));
   364‚Üí        });
   365‚Üí
   366‚Üí        child.on('exit', async (code) => {
   367‚Üí          // Remove signal handlers to prevent memory leaks
   368‚Üí          process.off('SIGINT', sigintHandler);
   369‚Üí          process.off('SIGTERM', sigtermHandler);
   370‚Üí
   371‚Üí          // Show shutting down message
   372‚Üí          console.log(''); // Empty line for spacing
   373‚Üí          console.log(chalk.yellow('Shutting down...'));
   374‚Üí
   375‚Üí          // Grace period: wait for any final API calls from the external agent
   376‚Üí          // Many agents (Claude, Gemini, Codex) send telemetry/session data on shutdown
   377‚Üí          if (this.proxy) {
   378‚Üí            const gracePeriodMs = 2000; // 2 seconds
   379‚Üí            logger.debug(`[${this.displayName}] Waiting ${gracePeriodMs}ms grace period for final API calls...`);
   380‚Üí            await new Promise(resolve => setTimeout(resolve, gracePeriodMs));
   381‚Üí          }
   382‚Üí
   383‚Üí          // Finalize metrics on agent exit
   384‚Üí          if (this.metricsOrchestrator && code !== null) {
   385‚Üí            await this.metricsOrchestrator.onAgentExit(code);
   386‚Üí          }
   387‚Üí
   388‚Üí          // Lifecycle hook: session end (provider-aware)
   389‚Üí          if (code !== null) {
   390‚Üí            await executeOnSessionEnd(this, this.metadata.lifecycle, this.metadata.name, code, env);
   391‚Üí          }
   392‚Üí
   393‚Üí          // Clean up proxy
   394‚Üí          await cleanup();
   395‚Üí
   396‚Üí          // Lifecycle hook: afterRun (provider-aware)
   397‚Üí          if (code !== null) {
   398‚Üí            await executeAfterRun(this, this.metadata.lifecycle, this.metadata.name, code, env);
   399‚Üí          }
   400‚Üí
   401‚Üí          // Show goodbye message with random easter egg
   402‚Üí          console.log(chalk.cyan.bold(getRandomGoodbyeMessage()));
   403‚Üí          console.log(''); // Spacing before powered by
   404‚Üí          console.log(chalk.cyan('Powered by AI/Run CodeMie CLI'));
   405‚Üí          console.log(''); // Empty line for spacing
   406‚Üí
   407‚Üí          if (code === 0) {
   408‚Üí            resolve();
   409‚Üí          } else {
   410‚Üí            reject(new Error(`${this.displayName} exited with code ${code}`));
   411‚Üí          }
   412‚Üí        });
   413‚Üí      });
   414‚Üí    } catch (error) {
   415‚Üí      // Clean up proxy on error
   416‚Üí      if (this.proxy) {
   417‚Üí        await this.proxy.stop();
   418‚Üí        this.proxy = null;
   419‚Üí      }
   420‚Üí      throw error;
   421‚Üí    }
   422‚Üí  }
   423‚Üí
   424‚Üí  /**
   425‚Üí   * Check if proxy should be used for this agent/provider combination
   426‚Üí   */
   427‚Üí  private shouldUseProxy(env: NodeJS.ProcessEnv): boolean {
   428‚Üí    const providerName = env.CODEMIE_PROVIDER;
   429‚Üí    if (!providerName) return false;
   430‚Üí
   431‚Üí    const provider = ProviderRegistry.getProvider(providerName);
   432‚Üí    const isSSOProvider = provider?.authType === 'sso';
   433‚Üí    const isProxyEnabled = this.metadata.ssoConfig?.enabled ?? false;
   434‚Üí
   435‚Üí    return isSSOProvider && isProxyEnabled;
   436‚Üí  }
   437‚Üí
   438‚Üí  /**
   439‚Üí   * Build proxy configuration from environment variables
   440‚Üí   */
   441‚Üí  private buildProxyConfig(env: NodeJS.ProcessEnv): ProxyConfig {
   442‚Üí    // Get and validate target URL
   443‚Üí    const targetApiUrl = env.CODEMIE_BASE_URL;
   444‚Üí    if (!targetApiUrl) {
   445‚Üí      throw new Error('No API URL found for SSO authentication');
   446‚Üí    }
   447‚Üí
   448‚Üí    // Parse timeout (seconds ‚Üí milliseconds, default 0 = unlimited)
   449‚Üí    const timeoutSeconds = env.CODEMIE_TIMEOUT ? parseInt(env.CODEMIE_TIMEOUT, 10) : 0;
   450‚Üí    const timeoutMs = timeoutSeconds * 1000;
   451‚Üí
   452‚Üí    // Parse profile config from JSON
   453‚Üí    let profileConfig: CodeMieConfigOptions | undefined = undefined;
   454‚Üí    if (env.CODEMIE_PROFILE_CONFIG) {
   455‚Üí      try {
   456‚Üí        profileConfig = JSON.parse(env.CODEMIE_PROFILE_CONFIG) as CodeMieConfigOptions;
   457‚Üí      } catch (error) {
   458‚Üí        logger.warn('[BaseAgentAdapter] Failed to parse profile config:', error);
   459‚Üí      }
   460‚Üí    }
   461‚Üí
   462‚Üí    return {
   463‚Üí      targetApiUrl,
   464‚Üí      clientType: this.metadata.ssoConfig?.clientType || 'unknown',
   465‚Üí      timeout: timeoutMs,
   466‚Üí      model: env.CODEMIE_MODEL,
   467‚Üí      provider: env.CODEMIE_PROVIDER,
   468‚Üí      profile: env.CODEMIE_PROFILE_NAME,
   469‚Üí      integrationId: env.CODEMIE_INTEGRATION_ID,
   470‚Üí      sessionId: env.CODEMIE_SESSION_ID,
   471‚Üí      version: env.CODEMIE_CLI_VERSION,
   472‚Üí      profileConfig
   473‚Üí    };
   474‚Üí  }
   475‚Üí
   476‚Üí  /**
   477‚Üí   * Centralized proxy setup
   478‚Üí   * Works for ALL agents based on their metadata
   479‚Üí   */
   480‚Üí  protected async setupProxy(env: NodeJS.ProcessEnv): Promise<void> {
   481‚Üí    // Early return if proxy not needed
   482‚Üí    if (!this.shouldUseProxy(env)) {
   483‚Üí      return;
   484‚Üí    }
   485‚Üí
   486‚Üí    try {
   487‚Üí      // Build proxy configuration
   488‚Üí      const config = this.buildProxyConfig(env);
   489‚Üí
   490‚Üí      // Create and start the proxy
   491‚Üí      this.proxy = new CodeMieProxy(config);
   492‚Üí      const { url } = await this.proxy.start();
   493‚Üí
   494‚Üí      // Update environment with proxy URL
   495‚Üí      env.CODEMIE_BASE_URL = url;
   496‚Üí      env.CODEMIE_API_KEY = 'proxy-handled';
   497‚Üí    } catch (error) {
   498‚Üí      const errorMessage = error instanceof Error ? error.message : String(error);
   499‚Üí      throw new Error(`Proxy setup failed: ${errorMessage}`);
   500‚Üí    }
   501‚Üí  }
   502‚Üí
   503‚Üí  /**
   504‚Üí   * Extract agent config from environment
   505‚Üí   */
   506‚Üí  private extractConfig(env: NodeJS.ProcessEnv): AgentConfig {
   507‚Üí    return {
   508‚Üí      provider: env.CODEMIE_PROVIDER,
   509‚Üí      model: env.CODEMIE_MODEL,
   510‚Üí      baseUrl: env.CODEMIE_BASE_URL,
   511‚Üí      apiKey: env.CODEMIE_API_KEY,
   512‚Üí      timeout: env.CODEMIE_TIMEOUT ? parseInt(env.CODEMIE_TIMEOUT, 10) : undefined,
   513‚Üí      profileName: env.CODEMIE_PROFILE_NAME
   514‚Üí    };
   515‚Üí  }
   516‚Üí
   517‚Üí  /**
   518‚Üí   * Transform CODEMIE_* environment variables to agent-specific format
   519‚Üí   * based on agent's envMapping metadata.
   520‚Üí   *
   521‚Üí   * This is called automatically before lifecycle.beforeRun hook.
   522‚Üí   * Agents can still override this in their lifecycle hooks for custom logic.
   523‚Üí   *
   524‚Üí   * IMPORTANT: Clears existing agent-specific vars first to prevent
   525‚Üí   * contamination from previous shell sessions.
   526‚Üí   */
   527‚Üí  protected transformEnvVars(env: NodeJS.ProcessEnv): NodeJS.ProcessEnv {
   528‚Üí    const { envMapping } = this.metadata;
   529‚Üí
   530‚Üí    if (!envMapping) {
   531‚Üí      return env;
   532‚Üí    }
   533‚Üí
   534‚Üí    // Step 1: Clear all agent-specific env vars first to prevent contamination
   535‚Üí    // from previous shell sessions
   536‚Üí    if (envMapping.baseUrl) {
   537‚Üí      for (const envVar of envMapping.baseUrl) {
   538‚Üí        delete env[envVar];
   539‚Üí      }
   540‚Üí    }
   541‚Üí    if (envMapping.apiKey) {
